package com.analogics.ocr;import android.content.Context;import android.util.Pair;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import java.util.List;public class CustomAdapter extends ArrayAdapter<Pair<String, String>> {    public CustomAdapter(Context context, List<Pair<String, String>> objects) {        super(context, android.R.layout.simple_list_item_2, objects);    }    @NonNull    @Override    public View getView(int position, @Nullable View convertView, @NonNull ViewGroup parent) {        if (convertView == null) {            convertView = LayoutInflater.from(getContext()).inflate(android.R.layout.simple_list_item_2, parent, false);        }        Pair<String, String> item = getItem(position);        TextView text1 = convertView.findViewById(android.R.id.text1);        TextView text2 = convertView.findViewById(android.R.id.text2);        if (item != null) {            text1.setText(item.first);            text2.setText(item.second);        }        return convertView;    }}